    byte[][] readBMapFromFile(String filename) {
        byte[][] map = new byte[CP.R][CP.C];
        try {
            InputStream is = new FileInputStream("map" + File.separator + filename);
            BufferedReader br = new BufferedReader(new InputStreamReader(is));
            String line = "";
            int r = 0;//决定行号
            while ((line = br.readLine()) != null) {
                //循环遍历每一个元素填充一行的每一列
                String[] arr = line.split("\\s");
                for (int i = 0; i < arr.length; i++) {
                    map[r][i] = Byte.parseByte(arr[i]);
                }
                r++;//行号加1
            }
        } catch (FileNotFoundException e) {
            throw new RuntimeException(e);
        } catch (IOException e) {
            throw new RuntimeException(e);
        }
        return map;
    }
